import 'package:flutter/material.dart';
import 'home_timeline.dart';
import 'explore_page.dart';
import 'notifications_page.dart';

class TwitterTabs extends StatefulWidget {
  @override
  _TwitterTabsState createState() => _TwitterTabsState();
}

class _TwitterTabsState extends State<TwitterTabs>
    with SingleTickerProviderStateMixin {
  TabController? _tabController;

  @override
  void initState() {
    super.initState();
    _tabController = TabController(vsync: this, length: 3);
    _tabController!.addListener(() {
      setState(() {});
    });
  }

  @override
  void dispose() {
    _tabController?.dispose();
    super.dispose();
  }

  // Function to handle FAB button click
  void _handleFabClick(BuildContext context) {
    showDialog(
      context: context,
      builder: (BuildContext context) {
        return AlertDialog(
          title: Text('Compose Message'),
          content: TextField(
            decoration: InputDecoration(
              hintText: 'Type your message here...',
            ),
          ),
          actions: <Widget>[
            TextButton(
              child: Text('Cancel'),
              onPressed: () {
                Navigator.of(context).pop();
              },
            ),
            TextButton(
              child: Text('Send'),
              onPressed: () {
                // Handle sending the message here
                Navigator.of(context).pop();
              },
            ),
          ],
        );
      },
    );
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: Text('Twitter Inspired App'),
        bottom: TabBar(
          controller: _tabController,
          tabs: [
            Tab(icon: Icon(Icons.home), text: 'Home'),
            Tab(icon: Icon(Icons.explore), text: 'Explore'),
            Tab(icon: Icon(Icons.notifications), text: 'Notifications'),
          ],
        ),
      ),
      body: TabBarView(
        controller: _tabController,
        children: [
          HomeTimeline(),
          ExplorePage(),
          NotificationsPage(),
        ],
      ),
      floatingActionButton: FloatingActionButton(
        onPressed: () {
          _handleFabClick(context); // Call the _handleFabClick function
        },
        child: Icon(Icons.message),
      ),
    );
  }
}
